import React, {Component} from 'react';
import axios from 'axios';

export default class CreateUser extends Component {

    constructor(props) {
        super(props);
        this.state = {
            username: '',
            email: '',
            password: '',
            type: ''
        }

        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangeEmail = this.onChangeEmail.bind(this);
        this.onChangePassword = this.onChangePassword.bind(this);
        this.onChangeType = this.onChangeType.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }
    // <label>Type: </label>
    //                     <input type="text" 
    //                            className="form-control" 
    //                            value={this.state.type}
    //                            onChange={this.onChangeType}
    //                            /> 

    onChangeUsername(event) {
        this.setState({ username: event.target.value });
    }

    onChangeEmail(event) {
        this.setState({ email: event.target.value });
    }

    onChangePassword(event) {
        this.setState({ password: event.target.value });
    }

    onChangeType(event) {
        this.setState({ type: event.target.value });
    }

    onSubmit(e) {
        e.preventDefault();
        const createdUser = {
            username: this.state.username,
            email: this.state.email,
            password: this.state.password,
            type: this.state.type
        }

        axios.post('http://localhost:4000/add', createdUser)
             .then(res => console.log(res.data));

        this.setState({
            username: '',
            email: '',
            password: '',
            type: ''
        });
    }

    render() {
        return (
            <div>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>Username: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.username}
                               onChange={this.onChangeUsername}
                               />
                    </div>
                    <div className="form-group">
                        <label>Email: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.email}
                               onChange={this.onChangeEmail}
                               />  
                    </div>
                    <div className="form-group">
                        <label>Password: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.password}
                               onChange={this.onChangePassword}
                               />  
                    </div>
                    <div className="form-group">

                        <label for="C/V">Type:</label>
                        <div class="radio">
                        <label><input type="radio" id="C/V" value="Vendor"/>
                        Vendor</label></div>
                        <div class="radio">
                        <label>
                        <input type="radio" id="C/V"  value="Customer"/>
                        Customer</label>
                        </div>

                    </div>
                    <div className="form-group">
                        <input type="submit" value="Create User" className="btn btn-danger"/>
                    </div>
                </form>
            </div>
        )
    }
}
