import React, {Component} from 'react';
import axios from 'axios';

export default class CreateUser extends Component {

    constructor(props) {
        super(props);
        //inheriting the variables of parent class

        this.state = {
            name: '',
            //product name to be added
            quantity: '',
            //quantity of product
            username: this.props.match.params.id,
            //vendor name
            status: 'Not Dispatched',
            //so that goes to product list if quantity >0 and dispatch list if quantity =0
            price: ''


        }

        this.onChangePrice = this.onChangePrice.bind(this);
        this.onChangeName = this.onChangeName.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        this.onChangeQuantity = this.onChangeQuantity.bind(this);

    }

    onChangePrice(event) {
        this.setState({ price: event.target.value });
    }

    onChangeName(event) {
        this.setState({ name: event.target.value });
    }

    onSubmit(e) {
        //function adds the record to the database
        e.preventDefault();
        const Product = {
            name: this.state.name,
            quantity: this.state.quantity,
            username: this.state.username,
            status: "Not Dispatched",
            price: this.state.price           
        }
        //send the instance to backend to add to database
        axios.post('http://localhost:4000/vendor/add', Product)
             .then(res => console.log("Added"));

        this.setState({
            name: '',
            //product name to be added
            quantity: '',
            //quantity of product
            status: 'Not Dispatched',
            //so that goes to product list if quantity >0 and dispatch list if quantity =0
            price: ''
        });
    }


    onChangeQuantity(event) {
        this.setState({ quantity: event.target.value });
    }

    render() {
        return (
            <div>
                <form onSubmit={this.onSubmit}>

                    <div className="form-group">
                        <label>Name: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.name}
                               onChange={this.onChangeName}
                               />  
                    </div>



                    <div className="form-group">
                        <label>Price: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.price}
                               onChange={this.onChangePrice}
                               />  
                    </div>

                    <div className="form-group">
                        <label>Quantity: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.quantity}
                               onChange={this.onChangeQuantity}
                               />  
                    </div>

                    <div className="form-group">
                        <input type="submit" value="Add Product" className="btn btn-primary"/>
                    </div>
                </form>
            </div>
        )
    }
}
