import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'
export default class Vendor extends Component {
    constructor(props) {
        super(props);

        this.state = {
            vendor: this.props.match.params.id

        }

    }

    add(e){
        this.props.history.push
                        ({
                            pathname: "/vendor/add/" + this.state.vendor
                        })
    }

    product(e){
        this.props.history.push
                        ({
                            pathname: "/vendor/product/" + this.state.vendor
                        })
    }

    dispatch(e){
        this.props.history.push
                        ({
                            pathname: "/vendor/dispatch/" + this.state.vendor
                        })
    }

    dispatched(e){
        this.props.history.push
                        ({
                            pathname: "/vendor/dispatched/" + this.state.vendor
                        })
    }
    render(){
        return (
            <div>
            <p class="p-2 text-light bg-dark">Logged in as Vendor <b class="text-danger">{this.state.vendor}</b></p>
            <button type="button" class="p-3 m-3 btn btn-success" onClick={(e)=>this.add(e)}>Add product</button>
            <button type="button" class="p-3 m-3 btn btn-info" onClick={(e)=>this.product(e)}>Current Products</button>
            <button type="button" class="p-3 m-3 btn btn-warning" onClick={(e)=>this.dispatch(e)}>Dispatch</button>
            <button type="button" class="p-3 m-3 btn btn-danger" onClick={(e)=>this.dispatched(e)}>Dispatched</button>
            {/* <Button class="bg-primary" onClick={(e)=>this.add(e)}> Add Product </Button>
            <Button class="bg-success" onClick={(e)=>this.product(e)}> Current Products </Button>
            <Button class="success" onClick={(e)=>this.dispatch(e)}> Dispatch </Button>
            <Button class="dark" onClick={(e)=>this.dispatched(e)}> Dispatched </Button> */}
            </div>
        )
    }

}
