import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'
import {Button} from 'react-bootstrap';

export default class Vendor extends Component {
    constructor(props) {
        super(props);

        this.state = {
            vendor: this.props.match.params.id

        }

    }

    add(e){
        this.props.history.push
                        ({
                            pathname: "/vendor/add/" + this.state.vendor
                        })
    }

    product(e){
        this.props.history.push
                        ({
                            pathname: "/vendor/product/" + this.state.vendor
                        })
    }

    dispatch(e){
        this.props.history.push
                        ({
                            pathname: "/vendor/dispatch/" + this.state.vendor
                        })
    }

    dispatched(e){
        this.props.history.push
                        ({
                            pathname: "/vendor/dispatched/" + this.state.vendor
                        })
    }
    render(){
        return (
            <div>
            <p class="p-2 text-light bg-dark">Logged in as Vendor <b class="text-danger">{this.state.vendor}</b></p>
            <Button class="primary" onClick={(e)=>this.add(e)}> Add Product </Button>
            <Button class="info" onClick={(e)=>this.product(e)}> Current Products </Button>
            <Button class="success" onClick={(e)=>this.dispatch(e)}> Dispatch </Button>
            <Button class="dark" onClick={(e)=>this.dispatched(e)}> Dispatched </Button>
            </div>
        )
    }

}
