import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css'
import React, {Component} from 'react';

export default class AddVendor extends Component {

    constructor(props) {
        super(props);
        //inheriting the variables of parent class
        this.state = {
            //defining class object instance values            
            vendor: this.props.match.params.id,
            //vendor details are sent while the page is being rendered
            product: []
        }
    }

    componentDidMount() {

        //Setting initial values for the Product
        const Product={
            status: 'Not Dispatched',
            quantity: 0,
            username: this.state.vendor         
        }

        axios.post('http://localhost:4000/vendor/dispatch', Product)
        //data received from backend
             .then(res => {
                 //the products list is sent by /product and stored in our class instance
                 this.setState({product: res.data});
             })
             .catch((error) => {
                 //catching errors
                 console.log("Error: ", error)
             })
    }
    removeproduct(e,Prodname,Usernam)
    {
        const Product={
            name: Prodname,
            quantity: 0,
            status: 'Not Dispatched',
            username: Usernam        
        }
        //Product's initial value s
        axios.post('http://localhost:4000/vendor/dispatch_disp ', Product)
        //to cancel the product
             .then(res => {
                const Product={
                    status: 'Not Dispatched',
                    username: this.state.vendor,
                    quantity: 0                    
                }
                console.log(Product)
                axios.post('http://localhost:4000/vendor/dispatch', Product)
                     .then(response => {
                         this.setState({product: response.data});
                         console.log(response.data)
                     })
                     .catch(function(error) {
                         console.log(error);
                     })


                     this.props.history.push
                     ({
                          pathname: "/vendor/dispatch/" + this.state.Vendor
                     })
             })
             .catch(function(error) {
                 console.log(error);
             })


    }

    render() {
        return (
            <div>
                {/* <p>HELLO VENDOR {this.state.Vendor} </p> */}
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Price</th>
                            <th>Quantity</th>
                            <th> </th>
                        </tr>
                    </thead>
                    <tbody>
                    { 
                        this.state.product.map((currentProduct, i) => {
                            return (
                                <tr>
                                    <td>{currentProduct.name}</td>
                                    <td>{currentProduct.price}</td>
                                    <td>{currentProduct.quantity}</td>
                                    <td><Button variant="success" onClick={(e)=>this.disp(e,currentProduct.name,this.state.Vendor)} > Dispatch </Button> </td>
                                </tr>
                            )
                        })
                    }
                    </tbody>
                </table>
            </div>
        )
    }
}
